import React from 'react';
import './Total.css';

const Total = (props) => (
  <p>
    Number of exercises:{' '}
    {(() => {
      console.log(
        props.parts
          .map((part) => part.exercises)
          .reduce((total, value) => total + props[value], 0)
      );
    })()}
    {/* {(Object.keys(props).reduce((total, value) => total + props[value], 0))} */}
  </p>
);
export default Total;
